!function() {
    'use strict';

    // Get the login toggle element
    var loginToggle = document.getElementById('bpbLoginToggle');
    if (loginToggle) {
        // Add click event listener to login toggle
        loginToggle.addEventListener('click', function(e) {
            e.preventDefault();
            // toggle the expanded class on the login form element
            document.getElementById('bpbLoginForm').classList.toggle('expanded');
            // toggle the fa-angle-right and fa-angle-down classes on the toggle icon
            var toggleIcon = document.getElementById('bypassLoginToggle');
            toggleIcon.classList.toggle('fa-angle-right');
            toggleIcon.classList.toggle('fa-angle-down');
        });

        // If cookies are not enabled, add expanded class to cookie warning element
        if (!navigator.cookieEnabled) {
            document.getElementById('cookieWarning').classList.add('expanded');
        }
    }
}();

